import '../App.css';
import './CreateAccount.css';

import React, { Component } from "react";
import axios from 'axios';

export default class CreateAccount extends Component {
  constructor(props) {
    super(props);

    this.onChangeaccountName = this.onChangeaccountName.bind(this);
    this.onChangeaccountPosition = this.onChangeaccountPosition.bind(this);
    this.onChangeaccountLevel = this.onChangeaccountLevel.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      account_username: "",
      account_position: "",
      account_level: "",
    };
  }

  // These methods will update the state properties.
  onChangeaccountName(e) {
    this.setState({
      account_username: e.target.value,
    });
  }

  onChangeaccountPosition(e) {
    this.setState({
      account_position: e.target.value,
    });
  }

  onChangeaccountLevel(e) {
    this.setState({
      account_level: e.target.value,
    });
  }

// This function will handle the submission.
  onSubmit(e) {
    e.preventDefault();

    // When post request is sent to the create url, axios will add a new record(newaccount) to the database.
    const newaccount = {
      account_username: this.state.account_username,
      account_position: this.state.account_position,
      account_level: this.state.account_level,
    };

    axios
      .post("http://localhost:5000/record/add", newaccount)
      .then((res) => console.log(res.data))
      .then(document.location.pathname = "/summary");

    // We will empty the state after posting the data to the database
    this.setState({
      account_username: "",
      account_position: "",
      account_level: "",
    });
  }

  // This following section will display the form that takes the input from the user.
  render() {
    return (
      <div className="Create flex">
        <h3>Create New Record</h3>
        <form onSubmit={this.onSubmit}>
          <input type="text" className="form-control" value={this.state.account_username} onChange={this.onChangeaccountName}/>
          <input type="text" className="form-control" value={this.state.account_position} onChange={this.onChangeaccountPosition}/>
          <div className="form-group">
            <div className="form-check form-check-inline">
              <input
                className="form-check-input"
                type="radio"
                name="priorityOptions"
                id="priorityLow"
                value="Intern"
                checked={this.state.account_level === "Intern"}
                onChange={this.onChangeaccountLevel}
              />
              <label className="form-check-label">Intern</label>
            </div>
            <div className="form-check form-check-inline">
              <input
                className="form-check-input"
                type="radio"
                name="priorityOptions"
                id="priorityMedium"
                value="Junior"
                checked={this.state.account_level === "Junior"}
                onChange={this.onChangeaccountLevel}
              />
              <label className="form-check-label">Junior</label>
            </div>
            <div className="form-check form-check-inline">
              <input
                className="form-check-input"
                type="radio"
                name="priorityOptions"
                id="priorityHigh"
                value="Senior"
                checked={this.state.account_level === "Senior"}
                onChange={this.onChangeaccountLevel}
              />
              <label className="form-check-label">Senior</label>
            </div>
          </div>
          <div className="form-group">
            <input
              type="submit"
              value="Create account"
              className="btn btn-primary"
            />
          </div>
        </form>
      </div>
    );
  }
}
