import '../App.css';
import './Contact.css';

import React, { useRef} from 'react'

const Contact = (props) => {

    const firstNameRef = useRef(null);
    const lastNameRef = useRef(null);
    const emailRef = useRef(null);
    const messageRef = useRef(null);

    const submit = (event) => {
        event.preventDefault();
        const data = {
            firstName: firstNameRef.current.value,
            lastName: lastNameRef.current.value,
            email: emailRef.current.value,
            message: messageRef.current.value
        }
        alert(JSON.stringify(data))
    }

    return (
        <div className="Contact flex">
            <form onSubmit={submit} className="Contact-form flex">
                <div className="Name-container">
                    <input for="firstName" type="text" name="firstName" ref={firstNameRef} placeholder="First Name" tabIndex="1"></input>
                    <input for="lastName" type="text" name="lastName" ref={lastNameRef} placeholder="Last name" tabIndex="2"></input>
                </div>

                <input for="email" type="email" name="email" ref={emailRef} placeholder="Email"tabIndex="3"></input>


                <textarea name="message" ref={messageRef} placeholder="Type your message here" tabIndex="5"></textarea>

                <button type="submit">Send Message</button>
            </form>
        </div>
    );
}

export default Contact;
